using UnityEngine; public class RodRotator : MonoBehaviour{    [Header("Rod Rotation")]     private Quaternion _startRotation;    private float _rotationDif;        private bool _isRotated;    private void Start()    {        _startRotation = transform.rotation;    }        public void RotateRod(Quaternion targetRotation, float timeToRotate)    {        Quaternion currentRotation = transform.rotation;        Quaternion endRotation = Quaternion.Slerp(currentRotation, targetRotation, timeToRotate * Time.deltaTime);        transform.rotation = endRotation;        CheckIfRotated(currentRotation, targetRotation);    }        public void CheckIfRotated(Quaternion currentRotation, Quaternion targetRotation)    {        float angle = Quaternion.Angle(currentRotation, targetRotation);        if (angle <= 5f)        {            _isRotated = true;        }    }    public Quaternion GetStartRotation()    {        return _startRotation;    }    public Quaternion GetCurrentRotation()    {        return transform.rotation;    }    public bool GetIsRotated()    {        return _isRotated;    }    public void SetIsRotated(bool isRotated)    {        _isRotated = isRotated;    }            }